mapper.select:
SELECT 

FROM cus_user a


	AND  a.del_flag = '0'

		AND  a.id = #{id}


		AND a.login_name = #{loginName}


		AND a.password = #{password}

SELECT 

FROM cus_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cus_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
